import React from 'react';
import imdb from '../../assets/imdb.png';

const MovieInfo = (props) => {
  const { movie } = props;
  const genres = movie.genres;
  const genresList = genres.map((genre, index)=>{
    return(
      <ul key={index}>
        <li>{genre}</li>
        <li>/</li>
      </ul>
    )
  })

  return (
    <div className="movie-info">
      <div className="movie-title">
        <h1>{movie.title}</h1>
        <h2>{movie.year}</h2>
        {/* <h2>Action / Adventure</h2> */}
        {genresList}
      </div>
      <div className="torrent-quality">
        <em>Available in:</em>
        <div>
          <a className='download-torrent' href="">
            <span></span>
                  3d
                </a>
          <a className='download-torrent' href="">
            <span></span>
                  3d
                </a>
          <a className='download-torrent' href="">
            <span></span>
                  3d
                </a>
        </div>
      </div>
      <div className="imdb">
        <img src={imdb} alt="imdb"></img> <span>8.9</span>
      </div>
    </div>
  )
}

export default MovieInfo
