import React, { Component } from 'react'
import '../../styles/browseMovie.css';
import MoviesCollection from './MoviesCollection';

import axios from 'axios';
import SearchBox from './SearchBox';

class BrowseMovies extends Component {
  state = {
    allMovies: [],
    search:''
  }

  componentDidMount() {
    const page1 = axios.get('https://yts.mx/api/v2/list_movies.json?limit=50&page=1');
    const page2 = axios.get('https://yts.mx/api/v2/list_movies.json?limit=50&page=2')
    axios.all([page1, page2])
      .then(
        axios.spread((...res) =>{
          this.setState({
            ...this.state,
            allMovies: res[0].data.data.movies.concat(res[1].data.data.movies)
          })
        })
        )
      
  }

  handleInput = (e) =>{
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  handleSubmit = (e) =>{
    e.preventDefault();
    const searchItem = this.state.search;

    if(searchItem.text !==""){
      this.setState({
        ...this.state,
        search: searchItem
      })
    }
  }

  render() {
    // console.log(this.state.search);
    
    let filteredMovies = this.state.allMovies.filter(movie =>{
      return(movie.title.toLowerCase().includes(this.state.search.toLowerCase()))
    })
    console.log('final data is',filteredMovies);

    return (
      <div className="browse-movies-section">
        <SearchBox handleInput={this.handleInput} handleSubmit={this.handleSubmit} search={this.state.search}/>
        <MoviesCollection allMovies={filteredMovies} />

      </div>
    )
  }
}

export default BrowseMovies
