import React, { Component } from "react";
import Axios from "axios";
class Movies extends Component {
  state = {
    movies: []
  };
  // getMovies() {
  //   Axios.get(`https://yts.mx/api/v2/list_movies.jsonp`)
  //     .then(data => this.setState({ movies: data.data.data.movies }))
  //     .catch(err => console.log(err));
  // }
  getMovies() {
    const axios = require("axios");

    axios({
      "method": "GET",
      "url": "https://yts-am-torrent.p.rapidapi.com/list_upcoming.json",
      "headers": {
        "content-type": "application/octet-stream",
        "x-rapidapi-host": "yts-am-torrent.p.rapidapi.com",
        "x-rapidapi-key": "ef67b08353msh1c15ddd6b7d4aeap1209c9jsn8c8e57cff0c7",
        "useQueryString": true
      }
    })
      .then(data => this.setState({ movies: data.data.data.movies }))
      .catch(err => console.log(err))
  }

  componentDidMount() {
    this.getMovies();
  }
  handleClick = data => {
    this.props.recMovies(data);
  };
  render() {
    return (
      <div className="container mt-5">
        <h3 className="text-center text-white my-5">Movies List</h3>
        <div className="row">
          {this.state.movies.map((m, i) => {
            return (
              <div
                className="col-md-3 mt-4  card-hover"
                onClick={() => this.handleClick(m)}
                key={i}
              >
                <div className="card">
                  <img src={m.medium_cover_image} alt="" />
                  <div className="overlay">
                    <h4>{m.title} </h4>
                    <button>View Details</button>
                  </div>
                </div>

              </div>
            );
          })}
        </div>
      </div>
    );
  }
}

export default Movies;
