import React from 'react';
import profile from '../../assets/profile.jpg';
import moment from 'moment';

const Summary = (props) => {

  const { movie } = props;
  // console.log(movie);

  const castList = movie.cast ? (
    movie.cast.map((cast, index) => {
      return (
        <li key={index}>
          <a href={'https://www.imdb.com/name/nm' + cast.imdb_code + '/'}><img src={cast.url_small_image} /></a>
          <p><span>{cast.name} </span>as {cast.character_name}</p>
        </li>
      )
    })
  ) : null

  return (
    <div>
      <div className="movie-summary">
        <h3>Synopsis</h3>
        <p>{movie.description_full}</p>
        <div>
          Downloaded {movie.download_count} times <br />
          {/* Aug 30,2016 at 01:29 AM */}
          {moment(movie.date_uploaded).format('ll')}<span> at </span> {moment(movie.date_uploaded).format('LT')}
        </div>
      </div>

      <div className="movie-cast">
        <h3>Cast</h3>
        <ul>
          {castList}
        </ul>
      </div>
    </div>
  )
}

export default Summary
