import React, { Component } from 'react'
import '../../styles/browseMovie.css';
import movie from '../../assets/movie.jpg'
import star from '../../assets/star-icon green.svg'
import MoviesCollection from './MoviesCollection';

import axios from 'axios';

class BrowseMovies extends Component {
  state = {
    allMovies: []
  }

  componentDidMount() {
    axios.get('https://yts.mx/api/v2/list_movies.json?limit=50')
      .then(res => {
        this.setState({
          allMovies: res.data.data.movies
        })
      })
  }

  render() {
    const{ allMovies} = this.state;
    return (
      <div className="browse-movies-section">
        <div className="main-search">
          <div className="container">
            <form className="search-form">
              <div className="search-field">
                <p>Search Movies:</p>
                <input type="search" />
              </div>
              <div className="search-btn">
                <input type="submit" value="Search" />
              </div>
            </form>
          </div>
        </div>
<MoviesCollection allMovies = {allMovies}/>
        
      </div>
    )
  }
}

export default BrowseMovies
