import React, { Component } from 'react';
import img from '../../assets/movie.jpg'
import bg from '../../assets/background.jpg'
import '../../styles/movieDetail.css';

import MovieInfo from './MovieInfo';
import RelatedMovies from './RelatedMovies';

class MovieDetails extends Component {
  render() {
    return (
      <div className="container movie-detail-container clearfix">
        <div className="background-image"
          style={{background: 'url(' + bg + ') no-repeat center center'}} />
        <div className="background-overlay"></div>

        <div className='row movie-container'>
          <div className="movie-poster">
            <div className="card-container">
              <div className="card">
                <img src={img} alt="movie" className="image" />
              </div>
            </div>
            <button className="download-btn">Download</button>
          </div>
          <MovieInfo />

          <RelatedMovies />
        </div>
      </div>
    );
  }
}

export default MovieDetails;
