import React, { Component } from "react";
import Axios from "axios";
class Movies extends Component {
  state = {
    movies: []
  };
  getMovies() {
    Axios.get(`https://yts.mx/api/v2/list_movies.jsonp?`)
      .then((data) => {
        // console.log(data);
        this.setState({ movies: data.data.data.movies })
      })
      .catch(err => console.log(err));
  }
  componentDidMount() {
    this.getMovies();
  }
  handleClick = data => {
    this.props.recMovies(data);
    console.log('data', data);
  };
  render() {
    return (
      <div className="container mt-5">
        <h3 className="text-center text-white my-5">Movies List</h3>
        <div className="row">
          {this.state.movies.map((m, i) => {
            return (
              <div
                className="col-md-3 mt-4  card-hover"
                onClick={() => this.handleClick(m)}
                key={i}
              >
                <div className="card">
                  <img src={m.medium_cover_image} alt="" />
                  <div className="overlay">
                    <h4>{m.title} </h4>
                    <button>View Details</button>
                  </div>
                </div>

              </div>
            );
          })}
        </div>
      </div>
    );
  }
}

export default Movies;
