import React, { Component } from 'react';
import img from '../../assets/movie.jpg'
import src from '../../assets/medium-screenshot2.jpg';
import bg from '../../assets/background.jpg'
import '../../styles/movieDetail.css';

import MovieInfo from './MovieInfo';
import RelatedMovies from './RelatedMovies';
import Summary from './Summary';
import Axios from 'axios';

class MovieDetails extends Component {
  state={
    movieDetails: null
  }

  componentDidMount(){
    let id = this.props.match.params.id;
    Axios.get('https://yts.mx/api/v2/movie_details.json?movie_id=' + id)
      .then(res =>{
        this.setState({
          movieDetails: res.data.data.movie
        })
      })
  }

  render() {
    console.log(this.state.movieDetails.medium_cover_image);
    const movie = this.state;
    return (
      <div className="container movie-detail-container clearfix">
        <div className="background-image"
          style={{background: 'url(' + bg + ') no-repeat center center'}} />
        <div className="background-overlay"></div>

        <div className='row movie-container'>
          <div className="movie-poster">
            <div className="card-container">
              <div className="card">
                <div className="image-container">
                <img src={movie.medium_cover_image} alt={movie.title} className="image" />
                </div>
              </div>
            </div>
            <button className="download-btn">Download</button>
          </div>
          <MovieInfo />
          <RelatedMovies />
        </div>

        <div className="row screenshots-section">
          <div className="screenshot">
            <a href=""><img src={src} alt="screenshot"/></a>
          </div>
          <div className="screenshot">
            <a href=""><img src={src} alt="screenshot"/></a>
          </div>
          <div className="screenshot">
            <a href=""><img src={src} alt="screenshot"/></a>
          </div>
        </div>

        <div className="row summary-section clearfix">
          <Summary />
        </div>
      </div>
    );
  }
}

export default MovieDetails;
