import React, { Component } from 'react'
import '../../styles/browseMovie.css';
import MoviesCollection from './MoviesCollection';

import axios from 'axios';
import SearchBox from './SearchBox';

class BrowseMovies extends Component {
  state = {
    allMovies: [],
    search: '',
    page: 1
  }

  getData = () => {
    axios.get(`https://yts.mx/api/v2/list_movies.json?page=${this.state.page}&query_term=${this.state.search}`)
     .then(res => {
       console.log(res);
       this.setState({
         ...this.state,
         allMovies: res.data.data.movies
       })
     })
  }

  componentDidMount() {
    // const page1 = axios.get('https://yts.mx/api/v2/list_movies.json?limit=50&page=1');
    // const page2 = axios.get('https://yts.mx/api/v2/list_movies.json?limit=50&quality=3D&page=1')
    // const page3 = axios.get('https://yts.mx/api/v2/list_movies.json?limit=50&page=2')
    // const page4 = axios.get('https://yts.mx/api/v2/list_movies.json?limit=50&quality=3D&page=2')
    // const page5 = axios.get('https://yts.mx/api/v2/list_movies.json?limit=50&quality=3D&page=3')
    // const page6 = axios.get('https://yts.mx/api/v2/list_movies.json?limit=50&quality=3D&page=4')
    // const page7 = axios.get('https://yts.mx/api/v2/list_movies.json?limit=50&page=3')
    // axios.all([page1, page2, page3, page4, page5, page6, page7])
    //   .then(
    //     axios.spread((...res) => {
    //       this.setState({
    //         ...this.state,
    //         allMovies: res[0].data.data.movies.concat(
    //           res[1].data.data.movies, res[2].data.data.movies,
    //           res[3].data.data.movies, res[4].data.data.movies,
    //           res[5].data.data.movies, res[6].data.data.movies)
    //       })
    //     })
    //   )

    this.getData();

  }

  handleInput = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  handleSubmit = (e) => {
    axios.get(`https://yts.mx/api/v2/list_movies.json?page=${this.state.page}&query_term=${this.state.search}`)
     .then(res => {
       console.log(res);
       this.setState({
         ...this.state,
         allMovies: res.data.data.movies
       })
     })
    e.preventDefault();
    // const searchItem = this.state.search;

    // if (searchItem.text !== "") {
    //   this.setState({
    //     ...this.state,
    //     search: searchItem
    //   })
    // }
  }

  render() {
    // console.log(this.state.allMovies);

    // let filteredMovies = this.state.allMovies.filter(movie => {
    //   return (movie.title.toLowerCase().includes(this.state.search.toLowerCase()))
    // })
    // console.log(filteredMovies);

    return (
      <div className="browse-movies-section">
        <SearchBox handleInput={this.handleInput} handleSubmit={this.handleSubmit} search={this.state.search} />
        <MoviesCollection allMovies={this.state.allMovies} />
      </div>
    )
  }
}

export default BrowseMovies
