import React, { Component } from 'react'
import '../../styles/browseMovie.css';
import MoviesCollection from './MoviesCollection';

import axios from 'axios';
import SearchBox from './SearchBox';
import Pagination from './Pagination'

class BrowseMovies extends Component {
  state = {
    allMovies: [],
    search: '',
    totalMovies: 0,
    currentPage:1
  }

  getData = () => {
    axios.get(`https://yts.mx/api/v2/list_movies.json?`)
     .then(res => {
       console.log(res);
       this.setState({
         ...this.state,
         allMovies: res.data.data.movies
       })
     })
  }

  componentDidMount() {
    this.getData();

  }

  handleInput = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  handleSubmit = (e) => {
    axios.get(`https://yts.mx/api/v2/list_movies.json?page=${this.state.page}&query_term=${this.state.search}`)
     .then(res => {
       console.log(res);
       this.setState({
         ...this.state,
         allMovies: res.data.data.movies,
         totalMovies: res.data.data.movie_count
       })
     })
    e.preventDefault();
    // const searchItem = this.state.search;

    // if (searchItem.text !== "") {
    //   this.setState({
    //     ...this.state,
    //     search: searchItem
    //   })
    // }
  }

  nextPage=(pageNumber) =>{
    axios.get(`https://yts.mx/api/v2/list_movies.json?page=${this.state.page}&query_term=${this.state.search}&limit=12&page=${pageNumber}`)
     .then(res => {
       console.log(res);
       this.setState({
         ...this.state,
         allMovies: res.data.data.movies,
         currentPage: pageNumber
       })
     })
  }

  render() {
    // console.log(this.state.allMovies);

    // let filteredMovies = this.state.allMovies.filter(movie => {
    //   return (movie.title.toLowerCase().includes(this.state.search.toLowerCase()))
    // })
    // console.log(filteredMovies);

    const numberPages = Math.floor(this.state.totalMovies / 20);

    return (
      <div className="browse-movies-section">
        <SearchBox handleInput={this.handleInput} handleSubmit={this.handleSubmit} search={this.state.search} />
        <MoviesCollection allMovies={this.state.allMovies} />
        {this.state.totalMovies > 20 ? <Pagination pages={numberPages} nextPage={this.nextPage} currentPage={this.state.currentPage} /> : ''}
      </div>
    )
  }
}

export default BrowseMovies
